<?php/*|===========================================================================||	MODELO DE DADOS PARA A TABELA           						        ||===========================================================================||																			||	VERSÃO:								1.0.0								||	 																		|	|===========================================================================|*/	Class usuariosModel extends Model {		public $_tabela = "usuarios";	//	ATRIBUTO PARA SETAR QUAL A TABELA QUE ESTÁ EM USO			# ---------------------------------------------------------------------------------------------------------------------------------	# METODO 001: [ PUBLIC FUNCTION LISTA REGISTROS ]	# ESTE METODO É RETORNA TODOS OS REGISTROS DO BANCO DE DADOS	# ---------------------------------------------------------------------------------------------------------------------------------				public function listausuarios($condicao = null, $qtd = null, $offset = null, $ordem = 'id ASC') {			return $this->read($condicao, $qtd, $offset, $ordem);		}			# ---------------------------------------------------------------------------------------------------------------------------------	# METODO 002: [ PUBLIC FUNCTION MOSTRA REGISTRO ]	# ESTE METODO RETORNA UM ÚNICO REGISTRO	# ---------------------------------------------------------------------------------------------------------------------------------						public function usuariosShow($where) {			return $this->read($where, null, null, 'id ASC');		}	# ---------------------------------------------------------------------------------------------------------------------------------		# METODO DE VALIDACAO DE DADOS: parametro formulairo	# ---------------------------------------------------------------------------------------------------------------------------------			public function ValidaDados($form,$campos) {			global $start;						// criando as sessoes para recuperar os dados do usuario:			for ($ct=0;$ct<count($campos);$ct++){			// EVITANDO SQL_INJECTION:				if (isset($_POST[$campos[$ct]]) ) {					$_POST[$campos[$ct]] = $start->NoSqlInject($_POST[$campos[$ct]]);				}				$_SESSION[$campos[$ct]] = (isset($_POST[$campos[$ct]]) ? $_POST[$campos[$ct]] : "");			}				$_SESSION['posic_erro'] = 0;	//	PONTEIRO PARA GERENCIAR VALIDACOES...						// VALIDACAO NOME - MINIMO 5 MÁXIMO 70.			$this->VerErro( $this->ValidarStringMinimoMaximo('nome', $_POST['nome'], 5, 70, null, null) );						// VALIDANDO BAIRRO, NÃO PODE SER VAZIO!			$this->VerErro( $this->ValidarVazio('bairro',$_POST['bairro'], null) );			// VALIDANDO CAMPO CIDADE:			$this->VerErro( $this->ValidarStringMinimoMaximo('cidade', $_POST['cidade'], 3, 70, null, null) );						// VALIDANDO ENDEREÇO:			$this->VerErro( $this->ValidarStringMinimoMaximo('endereco', $_POST['endereco'], 4, 70, null, null) );			// VALIDANDO CELULAR:			$this->VerErro( $this->ValidarStringNumerica('celular',$_POST['celular'], null,array("-","(",")") ));						// VALIDANDO TELEFONE RESIDENCIAL			$this->VerErro( $this->ValidaTel("telefone residencial", $_POST['telresidencial'], null) );						// VALIDANDO TELEFONE CELULAR			$this->VerErro( $this->ValidaTel("celular", $_POST['celular'], $msgErro = null) );									// VALIDANDO CPF:			$this->VerErro( $this->ValidaCPF('cpf',$_POST['cpf'], null) );						// VALIDANDO TELEFONE COMERCIAL:			$this->VerErro( $this->ValidaTel("telefone comercial", $_POST['telcomercial'], $msgErro = null) );						// VALIDANDO DATA:			$this->VerErro( $tmp = $this->ValidarData('data do cadastro',$_POST['datacad'], null, null, null) );						// VALIDANDO CEP:			$this->VerErro( $tmp = $this->ValidarCEP('cep', $_POST['CEP'], null) );			//  VALIDANDO E-MAIL:			$this->VerErro( $tmp = $this->ValidaEmail("email", $_POST['email'], null) );					// $tmp = $this->ValidarHora('complemento', $_POST['complemento'], null, null, null);			// $tmp = $this->ValidarDataHora('obs', $_POST['obs'], null, null, null, null, null);			//	$tmp = $this->ValidaCNPJ('bairro',$_POST['bairro'], null);						if ( isset($_SESSION["ERRO0"]) ) {	//	se existe ao menos um erro...				return array('1');	//	retorna o vetor de erros...					} else {				return array();		//	retorna o vetor de erros VAZIO								}			}			}	//	final da classe	?>